import React, { Component } from 'react';
import './App.css';
import Person from './Profile/Profile';
import person from './Profile/Profile';

class App extends Component{

  state = {
    persons:[
      {
        name:"Cristiano",
        age:"35"  
      },{
        name:"Memphis",
        age:"27"  
      }
    ],
    hiddenProperty:false  
  }
  switchName = () =>{
//    console.log("Button Clicked")
    if(!this.state.hiddenProperty){
      this.setState({
        persons:[
          {
            name:"Cristiano Ronaldo",
            age:"34",
            id: "cr7"  
          },{
            name:"Memphis Depay",
            age:"28",
            id:"md9"  
          }
        ],
        hiddenProperty:true    
      });  
    }else{
      this.setState({
        persons:[
          {
            name:"Cristiano Ronaldo",
            age:"34",
            id:"cr7" 
          },{
            name:"Memphis Depay",
            age:"28",
            id:"md9"  
          }
        ],
        hiddenProperty:false    
      });  
    }
  }
  inputChanged = (event) =>{
    console.log(event.target.value);
    this.setState({
      persons:[
        {
          name:"Cristiano Ronaldo",
          age:"34",
          id:"cr7"
        },{
          name:event.target.value,
          age:"28",
          id:"md9"  
        }
      ],
      hiddenProperty:false  
    });

  }
  deletePerson = (personIndex) =>{
    console.log("Person to Delete: "+ personIndex);
    const persons = this.state.persons;
    persons.splice(personIndex,1)
    this.setState({persons:persons});
  }
  render(){
    return (
      <div className="App">
        <h1> Hi, This is my Blog </h1>
        <p> You'll see details about me here! </p>
        <p> I am {Math.ceil((Math.random() * 30)*10)/10} years old</p>
        <div hidden={this.state.hiddenProperty}>
          {this.state.persons.map((person, index)=>{
            return (
                    <Person 
                      click={()=>this.deletePerson(index)}
                      name={person.name} 
                      age={person.age}
                      key={person.id}
                    ></Person>
                   )
          })}
          {/* <Person nameChange={this.inputChanged} click={this.switchName} name={this.state.persons[1].name} age={this.state.persons[1].age}/> */}
        </div>
        <button className="Button" onClick={this.switchName}>Show Person</button>
      </div>
    );  
  }
}

export default App;
