import React, { Component } from 'react';
import './App.css';
import Person from './Profile/Profile';

class App extends Component{

  state = {
    persons:[
      {
        name:"Cristiano",
        age:"35"  
      },{
        name:"Memphis",
        age:"27"  
      }
    ]
  }
  switchName = () =>{
//    console.log("Button Clicked")
    this.state.persons[0].name = "Cristiano Ronaldo";
    this.state.persons[0].age = "34";
    this.state.persons[1].name = "Memphis Depay";
    this.state.persons[1].age = "28";
  }
  render(){
    return (
      <div className="App">
        <h1> Hi, This is my Blog </h1>
        <p> You'll see details about me here! </p>
        <p> I am {Math.ceil((Math.random() * 30)*10)/10} years old</p>
        <Person name={this.state.persons[0].name} age={this.state.persons[0].age}></Person>
        <Person name={this.state.persons[1].name} age={this.state.persons[1].age}/>
        <button onClick={this.switchName}>Switch Button</button>
      </div>
    );  
  }
}

export default App;
