import React, { Component } from 'react';
import './App.css';
import Person from './Profile/Profile';

class App extends Component{

  state = {
    persons:[
      {
        name:"Cristiano",
        age:"35"  
      },{
        name:"Memphis",
        age:"27"  
      }
    ]
  }
  switchName = () =>{
//    console.log("Button Clicked")
    this.setState({
      persons:[
        {
          name:"Cristiano Ronaldo",
          age:"34"  
        },{
          name:"Memphis Depay",
          age:"28"  
        }
      ]  
    });
  }
  render(){
    return (
      <div className="App">
        <h1> Hi, This is my Blog </h1>
        <p> You'll see details about me here! </p>
        <p> I am {Math.ceil((Math.random() * 30)*10)/10} years old</p>
        <Person name={this.state.persons[0].name} age={this.state.persons[0].age}></Person>
        <Person click={this.switchName} name={this.state.persons[1].name} age={this.state.persons[1].age}/>
        <button onClick={this.switchName}>Switch Button</button>
      </div>
    );  
  }
}

export default App;
