import React, { Component } from 'react';
import './App.css';
import Person from './Profile/Profile';
import person from './Profile/Profile';

class App extends Component{

  state = {
    persons:[
      {
        name:"Cristiano",
        age:"35",
        id:"cr7"
      },{
        name:"Memphis",
        age:"27",
        id:"md9"  
      }
    ],
    hiddenProperty:false  
  }
  switchName = () =>{
  }
  inputChanged = (event, key) =>{
    console.log("Key: "+key);
    console.log(event.target.value);
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === key;
    })

    const resultantPerson = {
      ...this.state.persons[personIndex]
    }
    resultantPerson.name = event.target.value;
    const persons = [...this.state.persons];
    persons[personIndex] = resultantPerson;


    this.setState({
      persons:persons,
      hiddenProperty:false  
    });

  }
  deletePerson = (personIndex) =>{
    console.log("Person to Delete: "+ personIndex);
    const persons = this.state.persons;
    persons.splice(personIndex,1)
    this.setState({persons:persons});
  }
  render(){
    return (
      <div className="App">
        <div hidden={this.state.hiddenProperty}>
          {this.state.persons.map((person, index)=>{
            return (
                    <Person 
                      click={()=>this.deletePerson(index)}
                      name={person.name} 
                      age={person.age}
                      key={person.id}
                      nameChange={(event)=>this.inputChanged(event, person.id)}
                    ></Person>
                   )
          })}
        </div>
        <button className="Button" onClick={this.switchName}>Show Person</button>
      </div>
    );  
  }
}

export default App;
