  state = {
    persons:[
      {
        name:"Cristiano",
        age:"35"  
      },{
        name:"Memphis",
        age:"27"  
      }
    ],
    hiddenProperty:false  
  }
  switchName = () =>{
//    console.log("Button Clicked")
    if(!this.state.hiddenProperty){
      this.setState({
        persons:[
          {
            name:"Cristiano Ronaldo",
            age:"34"  
          },{
            name:"Memphis Depay",
            age:"28"  
          }
        ],
        hiddenProperty:true    
      });  
    }else{
      this.setState({
        persons:[
          {
            name:"Cristiano Ronaldo",
            age:"34"  
          },{
            name:"Memphis Depay",
            age:"28"  
          }
        ],
        hiddenProperty:false    
      });  
    }
  }
  inputChanged = (event) =>{
    console.log(event.target.value);
    this.setState({
      persons:[
        {
          name:"Cristiano Ronaldo",
          age:"34"  
        },{
          name:event.target.value,
          age:"28"  
        }
      ],
      hiddenProperty:false  
    });

  }
